import React from "react";
import axios from "./API/axios";

class Calculations extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstNum: "",
      secondNum: ""
    };
  }

  handleSubmit = event => {
    const sum = this.state.firstNum + this.state.secondNum;

    axios
      .get("/calculations/add", {
        params: {
          first_num: this.state.firstNum,
          second_num: this.state.secondNum,
          sum_result: sum
        }
      })
      .then(console.log("It worked!"));
  };

  handleChange = event => {
    let name = event.target.name;
    this.setState({ [name]: event.target.value });
  };

  displayForm = () => {
    return (
      <form onSubmit={this.handleSubmit}>
        <div className="form-row" id="container">
          <div className="col-5">
            <input
              type="number"
              className="form-control"
              placeholder="First Number"
              name="firstNum"
              value={this.state.firstNum ? this.state.firstNum : ""}
              onChange={this.handleChange}
              required
            />
          </div>
          <div className="col-5">
            <input
              type="number"
              className="form-control"
              placeholder="Second Number"
              name="secondNum"
              value={this.state.secondNum ? this.state.secondNum : ""}
              onChange={this.handleChange}
              required
            />
          </div>
          <div className="col-2">
            <button type="submit" className="btn btn-primary">
              Calculate
            </button>
          </div>
        </div>
      </form>
    );
  };

  render() {
    return <div>{this.displayForm()}</div>;
  }
}

export default Calculations;
