import React from "react";

class Calculations extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstNum: null,
      secondNum: null
    };
  }

  render() {
    return (
      <div>
        this.inputForm()
        <GetASum />
        <GetAProduct />
        <GetAPower />
      </div>
    );
  }

  handleSubmit = event => {
    console.log(this.state.firstNum, this.state.secondNum);
    event.preventDefault();
  };

  handleChange = event => {
    const name = event.target.name;
    this.setState({ [name]: event.target.value });
  };

  inputForm = () => {
    return (
      <form onSubmit={this.handleSubmit}>
        <div class="form-row" id="container">
          <div class="col-5">
            <input
              type="number"
              class="form-control"
              placeholder="First Number"
              name="firstNum"
              value={this.state.firstNum}
              onChange={this.handleChange}
              required
            />
          </div>
          <div class="col-5">
            <input
              type="number"
              class="form-control"
              placeholder="Second Number"
              name="secondNum"
              value={this.state.secondNum}
              onChange={this.handleChange}
              required
            />
          </div>
          <div class="col-2">
            <button type="submit" class="btn btn-primary">
              Calculate
            </button>
          </div>
        </div>
      </form>
    );
  };
}

export default Calculations;
