import React from "react";

class Calculations extends React.Component {
  constructor() {
    state = {
      firstNum: null,
      secondNum: null
    };
  }

  render() {
    inputForm();
  }

  inputForm = () => {
    return (
      <form>
        <div class="form-row" id="container">
          <div class="col-5">
            <input
              type="number"
              class="form-control"
              placeholder="First Number"
              required
            />
          </div>
          <div class="col-5">
            <input
              type="number"
              class="form-control"
              placeholder="Second Number"
              required
            />
          </div>
          <div class="col-2">
            <button type="submit" class="btn btn-primary">
              Calculate
            </button>
          </div>
        </div>
      </form>
    );
  };
}

export default Calculations;
