import React from "react";
import axios from "./API/axios";

class Calculations extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstNum: "",
      secondNum: ""
    };
  }

  handleSubmit = event => {
    event.preventDefault();

    //Caculations we use to insert into the datab
    const sum = Number(this.state.firstNum) + Number(this.state.secondNum);
    const product = sum * 2;
    const power = Math.exp(product, 2);

    //TODO: Update the nodemon file to add the product and power stuff
    //Then .then to complete those interactions as well

    axios
      .get("/calculations/add", {
        params: {
          first_num: this.state.firstNum,
          second_num: this.state.secondNum,
          sum_result: sum
        }
      })
      .then(
        axios.get("/calculations/product", {
          params: {
            product_result: product
          }
        })
      )
      .then(
        axios.get("/calculations/power", {
          params: {
            power_result: power
          }
        })
      );
  };

  handleChange = event => {
    let name = event.target.name;
    this.setState({ [name]: event.target.value });
  };

  displayForm = () => {
    return (
      <form onSubmit={this.handleSubmit}>
        <div className="form-row" id="container">
          <div className="col-5">
            <input
              type="number"
              className="form-control"
              placeholder="First Number"
              name="firstNum"
              value={this.state.firstNum ? this.state.firstNum : ""}
              onChange={this.handleChange}
              required
            />
          </div>
          <div className="col-5">
            <input
              type="number"
              className="form-control"
              placeholder="Second Number"
              name="secondNum"
              value={this.state.secondNum ? this.state.secondNum : ""}
              onChange={this.handleChange}
              required
            />
          </div>
          <div className="col-2">
            <button type="submit" className="btn btn-primary">
              Calculate
            </button>
          </div>
        </div>
      </form>
    );
  };

  render() {
    return <div>{this.displayForm()}</div>;
  }
}

export default Calculations;
